#include <stdint.h>
#include "font.h"

int char_width(char c, const struct font *f)
{
    int i;
    uint16_t all = 0;;

    for (int i = 0; i < f->h; i++)
        all |= f->data[(c - ' ') * f->h + i];

    for (i = 0; i < f->w; i++) {
        all = all >> 1;
        if (!all)
            return i;
    }

    return f->w;
}

